import Forecast from "./components/Forecast"
import Search from "./components/Search"
import useForecast from "./hooks/useForecast"

const App = (): JSX.Element => {

  const {
    term, options, forecast, onInputChange, onOptionSelect, onSubmit
  } = useForecast()

  return (
    <main className="flex justify-center items-center bg-gradient-to-r from-gray-700 via-gray-900 to-black h-auto w-full p-4">
      {forecast ? (
        <Forecast data={forecast} />
      ) : (
        <Search term={term} options={options} onInputChange={onInputChange} onOptionSelect={onOptionSelect} onSubmit={onSubmit} />
      )}
    </main>
  )
}

export default App
