import React from 'react'

const MainCard = ({ mode, children }: { mode: Boolean, children: JSX.Element | JSX.Element[] }): JSX.Element => {
    return (
        <main className={mode ? "flex justify-center items-center bg-gradient-to-br from-sky-400 via-rose-400 to-lime-400 h-[100vh] w-full p-4" : "flex justify-center items-center bg-gradient-to-br from-gray-700 via-gray-900 to-black h-[100vh] w-full p-4"}>
            {mode ? (
                <button className="bg-slate-700 hover:bg-slate-800 text-white font-bold py-2 px-4 rounded-full text-xl" onClick={handleDarkLightMode}>☾</button>
            ) : (
                <button className="bg-slate-200 hover:bg-slate-300 text-slate-700 font-bold py-2 px-4 rounded-full text-xl" onClick={handleDarkLightMode}>☀</button>
            )}
            {children}
        </main >
    )
}


export default MainCard
