import { useState, useEffect, ChangeEvent } from "react"
import { optionType } from "../types"

const useForecast = () => {
    const [term, setTerm] = useState<string>('')
    const [city, setCity] = useState<optionType | null>(null)
    const [options, setOptions] = useState<[]>([])
    const [forecast, setForecast] = useState<null>(null)

    const getSearchOptions = (value: string) => {
        fetch(
            `http://api.openweathermap.org/geo/1.0/direct?q=${value.trim()}&limit=5&appid=4d9b05e5e16eb293b0beb573d8a928d7`
        )
            .then((res) => res.json())
            .then((data) => setOptions(data))
    }



    const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
        const value = e.target.value.trim()
        setTerm(value)

        if (value === '') return


        getSearchOptions(value)
    }

    const getForecast = (city: optionType) => {
        fetch(
            `https://api.openweathermap.org/data/2.5/weather?lat=${city.lat}&lon=${city.lon}&units=metric&appid=4d9b05e5e16eb293b0beb573d8a928d7`
        )
            .then(res => res.json())
            .then(data => setForecast(data))
    }

    const onSubmit = () => {
        if (!city) return

        getForecast(city)
    }

    const onOptionSelect = (option: optionType) => {
        setCity(option)
    }

    useEffect(() => {

        if (city) {
            setTerm(city.name)
            setOptions([])
        }

    }, [city])

    return {
        term, options, forecast, onInputChange, onOptionSelect, onSubmit
    }
}

export default useForecast
