import Forecast from "./components/Forecast"
import Search from "./components/Search"
import useForecast from "./hooks/useForecast"
import { useState } from "react"
import MainCard from "./components/MainCard"

const App = (): JSX.Element => {

  const {
    term, options, forecast, onInputChange, onOptionSelect, onSubmit
  } = useForecast()

  const [mode, setMode] = useState<Boolean>(true)

  const handleDarkLightMode = () => {
    if (mode) {
      setMode(false)
      return
    }
    setMode(true)
  }


  return (
    <MainCard mode={mode}>
      {mode ? (
        <button className="absolute top-4 left-4 bg-slate-700 hover:bg-slate-800 text-white font-bold py-2 px-4 rounded-full" onClick={handleDarkLightMode}>â˜¾</button>
      ) : (
        <button className="absolute top-4 left-4 bg-slate-200 hover:bg-slate-300 text-slate-700 font-bold py-2 px-4 rounded-full" onClick={handleDarkLightMode}>Light</button>
      )}

      {
        forecast ? (
          <Forecast mode={mode} data={forecast} />
        ) : (
          <Search mode={mode} term={term} options={options} onInputChange={onInputChange} onOptionSelect={onOptionSelect} onSubmit={onSubmit} />
        )
      }
    </MainCard >
  )
}

export default App


