import { ChangeEvent, useEffect, useState } from "react"
import Search from "./components/Search"
import { optionType } from "./types"

const App = (): JSX.Element => {

  // http://api.openweathermap.org/geo/1.0/direct?q={city name},{state code},{country code}&limit={limit}&appid={API key}

  const [term, setTerm] = useState<string>('')
  const [city, setCity] = useState<optionType | null>(null)
  const [options, setOptions] = useState<[]>([])

  const getSearchOptions = (value: string) => {
    fetch(
      `http://api.openweathermap.org/geo/1.0/direct?q=${value.trim()}&limit=5&appid=4d9b05e5e16eb293b0beb573d8a928d7`
    )
      .then((res) => res.json())
      .then((data) => setOptions(data))
  }



  const onInputChange = (e: ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value.trim()
    setTerm(value)

    if (value === '') return


    getSearchOptions(value)
  }

  const getForecast = (city: optionType) => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?lat=${city.lat}&lon=${city.lon}&units=metric&appid=4d9b05e5e16eb293b0beb573d8a928d7`
    )
      .then(res => res.json())
      .then(data => console.log({ data }))
  }

  const onSubmit = () => {
    if (!city) return

    getForecast(city)
  }

  const onOptionSelect = (option: optionType) => {
    setCity(option)
  }

  useEffect(() => {

    if (city) {
      setTerm(city.name)
      setOptions([])
    }

  }, [city])

  return (
    <main className="flex justify-center items-center bg-gradient-to-br from-sky-400 via-rose-400 to-lime-400 h-[100vh] w-full">

      <Search term={""} options={[]} onInputChange={function (e: ChangeEvent<HTMLInputElement>): void {
        throw new Error("Function not implemented.")
      }} onOptionSelect={function (option: optionType): void {
        throw new Error("Function not implemented.")
      }} onSubmit={function (): void {
        throw new Error("Function not implemented.")
      }} />
    </main>
  )
}

export default App
